'use strict';

// MODULES //

var copy = require( 'fs' ).copyFile; // eslint-disable-line node/no-unsupported-features/node-builtins
var createReadStream = require( 'fs' ).createReadStream;
var createWriteStream = require( 'fs' ).createWriteStream;
var semver = require( 'semver' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;
var isBuffer = require( '@stdlib/assert/is-buffer' );
var isFunction = require( '@stdlib/assert/is-function' );
var VERSION = require( '@stdlib/process/node-version' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Asynchronously copies src to dest.By default, dest is overwritten if it already exists.
*
* @param {(string|Buffer)} src - source filename to copy
* @param {(string|Buffer)} dest - destination filename of the copy operation
* @param {(integer)} [mode] - modifiers for copy operation.
* @param {Function} clbk - callback to invoke after copying src to dest
* @throws {TypeError} src argument must be either a string or an instance of Buffer
* @throws {TypeError} dest argument must be either a string or an instance of Buffer
* @throws {TypeError} mode argument must be a number
* @throws {TypeError} callback argument must be a function
*
* @example
* function onCopy( error ) {
*     if ( error ) {
*         console.error( error.message );
*     }
*     console.log( 'src.txt has been copied to dest.txt' );
* }
*
* copyFile( 'src.txt', 'dest.txt', onCopy );
*/
function copyFile() {
	var src;
	var dest;
	var mode;
	var cb;
	var readStream;
	var writeStream;
	src = arguments[0] || null;
	dest = arguments[1] || null;
	if ( arguments.length < 4 ) {
		mode = null;
		cb = arguments[2] || null;
	} else {
		mode = arguments[2];
		cb = arguments[3];
	}
	if ( !isString( src ) && !isBuffer( src ) ) {
		throw new TypeError( format( 'invalid argument. The "src" argument must be of type string or an instance of Buffer. Received type: %s.', typeof src ) );
	}
	if ( !isString( dest ) && !isBuffer( dest ) ) {
		throw new TypeError( format( 'invalid argument. The "dest" argument must be of type string or an instance of Buffer. Received type: %s.', typeof dest ) );
	}
	if ( !isNumber( mode ) && mode !== null ) {
		throw new TypeError( format( 'invalid argument. The "mode" argument must be of type number. Received type: %s.', typeof mode ) );
	}
	if ( !isFunction( cb ) ) {
		throw new TypeError( format( 'invalid argument. The "cb" argument must be of type function. Received : `%s`.', cb ) );
	}
	if ( semver.gte( VERSION, '8.5.0' ) ) {
		copy( src, dest, mode, cb );
	} else {
		readStream = createReadStream( src );
		writeStream = createWriteStream( dest );
		readStream.on( 'error', function ( err ) {
			cb( err );
			readStream.destroy();
			writeStream.end();
		});
		writeStream.on( 'error', function ( err ) {
			cb( err );
			readStream.destroy();
			writeStream.end();
		});
		writeStream.on( 'finish', function () {
			cb( null ); // Success
			readStream.close();
			writeStream.close();
		});
		readStream.pipe( writeStream );
	}
}


// EXPORTS //

module.exports = copyFile;
